var siteUrl = "http://127.0.0.1:5500/videos/";

var periciaVideo = `${siteUrl}/video1.mp4`,
    periciaVideoTitle = "Per√≠cia",
    contabilidadeVideo = `${siteUrl}/video2.mp4`,
    contabilidadeVideoTitle = "Contabilidade",
    consultoriaVideo = `${siteUrl}/video3.mp4`,
    consultoriaVideoTitle = "Consultoria e Auditoria",
    advocaciaVideo = `${siteUrl}/video4.mp4`,
    advocaciaVideoTitle = "Advocacia e Paralegal",
    openbankVideo = `${siteUrl}/video5.mp4`,
    openbankVideoTitle = "OpenBank",
    empresasVideo = `${siteUrl}/video6.mp4`,
    empresasVideoTitle = "Empresas";

function resizeControls() {
    var e = $(window).width(),
        i = parseFloat(.8 * e);
    parseFloat(e / 10);
    $(".vjs-control-bar").css({ width: i + "px" })
} $(document).ready(function () {
    var e = videojs("video", {
        autoHeight: !1, autoWidth: !1
    });
    e.playlist([{
        name: "<span>" + periciaVideoTitle + "</span>",
        description: periciaVideoTitle, sources: [{
            src: periciaVideo,
            type: "video/mp4",
            id: "video1"
        }],
        thumbnail: !1
    }, {
        name: "<span>" + contabilidadeVideoTitle + "</span>",
        description: contabilidadeVideoTitle, sources: [{
            src: contabilidadeVideo,
            type: "video/mp4"
        }],
        thumbnail: !1
    }, {
        name: "<span>" + consultoriaVideoTitle + "</span>",
        description: consultoriaVideoTitle, sources: [{
            src: consultoriaVideo,
            type: "video/mp4"
        }],
        thumbnail: !1
    }, {
        name: "<span>" + advocaciaVideoTitle + "</span>",
        description: advocaciaVideoTitle, sources: [{
            src: advocaciaVideo,
            type: "video/mp4"
        }],
        thumbnail: !1
    }, {
        name: "<span>" + openbankVideoTitle + "</span>",
        description: openbankVideoTitle, sources: [{
            src: openbankVideo,
            type: "video/mp4"
        }],
        thumbnail: !1
    }, {
        name: "<span>" + empresasVideoTitle + "</span>",
        description: empresasVideoTitle, sources: [{
            src: empresasVideo,
            type: "video/mp4"
        }],
        thumbnail: !1
    }]),
        e.playlistUi({
            className: "tbwa-player", playOnSelect: 0
        }),
        e.playlist.autoadvance(0),
        resizeControls(),
        $("#main-video-player, #main-video-playlist").hide(),
        $("#tbwa-videos").on("click", function () {
            return $("#landing-video").hide(),
                $("#main-video-player, #main-video-playlist").show(),
                e.play(3)[3],
                resizeVideo("main-video-player"),
                !1
        })
}), $(window).on("resize", function () {
    resizeControls(), resizeVideo("main-video-player")
});